CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE IF NOT EXISTS "public"."balance"("player_address" text NOT NULL, "token_address" text NOT NULL, "block_height" integer NOT NULL, "amount" integer NOT NULL, "id" uuid NOT NULL DEFAULT gen_random_uuid(), PRIMARY KEY ("id") , FOREIGN KEY ("player_address") REFERENCES "public"."player"("ethereum_address") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("token_address") REFERENCES "public"."token"("address") ON UPDATE cascade ON DELETE cascade, UNIQUE ("id"));
