CREATE TABLE IF NOT EXISTS "public"."xp"("player_id" uuid NOT NULL, "token_address" text NOT NULL, "balance" integer NOT NULL, "id" uuid NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("player_id") REFERENCES "public"."player"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("token_address") REFERENCES "public"."token"("address") ON UPDATE cascade ON DELETE cascade, UNIQUE ("id"));
